examples.code:
generate "provider" {
  path      = "provider.tf"
  if_exists = "overwrite"
  contents = <<EOF
provider "aws" {
  region              = "us-east-1"
  version             = "= 2.3.1"
  allowed_account_ids = ["1234567890"]
}
EOF
}

terraform {
  before_hook "before_hook" {
    commands = ["apply", "plan"]
    execute = ["echo", "Running before hook"]
  }

  after_hook "after_hook" {
    commands = ["apply"]
    execute = ["echo", "Running after hook"]
    run_on_error = true
  }
}

locals {
  regions = ["us-east-1", "us-west-2", "eu-west-1"]
  region_to_bucket_name = {
    us-east-1 = "east-bucket"
    us-west-2 = "west-bucket"
    eu-west-1 = "eu-bucket"
  }
}

locals {
  region_hcl = read_terragrunt_config(find_in_parent_folders("region.hcl"))
  region = local.region_hcl.locals.region
  computed_value = run_cmd("--terragrunt-quiet", "python3", "-c", "print('Hello,')")
}

remote_state {
  backend = "s3"
  config = {
    bucket = "my-terraform-state"
    key    = "${path_relative_to_include()}/terraform.tfstate"
    region = "us-east-1"
    encrypt = true
    dynamodb_table = "my-lock-table"
  }
}

remote_state {
  backend = "gcs"
  config = {
    project = "my-project"
    location = "eu"
    bucket = "my-terraform-state"
    prefix = "${path_relative_to_include()}/terraform.tfstate"
  }
}

inputs = {
  map_string = {
    foo = "bar"
  }
  map_number = {
    foo = 42
    bar = 12345
  }
}

inputs = {
  object = {
    str  = "string"
    num  = 42
    list = [1, 2, 3]
    map = {
      foo = "bar"
    }
  }
}
